@app.post("/api/payments/webhook")
async def stripe_webhook(request: Request, db: Session = Depends(get_db)):
    payload = await request.body()
    sig_header = request.headers.get('stripe-signature')
    endpoint_secret = os.getenv("STRIPE_WEBHOOK_SECRET")
    try:
        event = stripe.Webhook.construct_event(payload, sig_header, endpoint_secret)
    except Exception as e:
        raise HTTPException(status_code=400, detail=f"Webhook error: {e}")

    if event['type'] == 'payment_intent.succeeded':
        intent = event['data']['object']
        user_id = intent['metadata'].get('user_id')
        if user_id:
            user = db.query(User).filter(User.id == int(user_id)).first()
            if user and user.role != 'premium':
                user.role = 'premium'
                user.subscription_valid_until = datetime.utcnow() + timedelta(days=30)
                db.commit()
    elif event['type'] == 'payment_intent.payment_failed':
        intent = event['data']['object']
        user_id = intent['metadata'].get('user_id')
        print(f"‚ùå Webhook FAILED: Payment failed for user ID {user_id}.")

    return {"status": "success"}



# --- TICKETS (ADMIN) ---

def analyze_ticket_content(subject: str, message: str) ->  Tuple[str, str]:
    """
    Analyse le contenu d'un ticket pour d√©terminer sa cat√©gorie et sa priorit√©.
    C'est une simulation d'IA simple.
    """
    text = (subject + " " + message).lower()
    
    # D√©termination de la priorit√©
    if "urgent" in text or "critical" in text or "down" in text:
        priority = "critical"
    elif "billing" in text or "payment" in text or "invoice" in text:
        priority = "high"
    else:
        priority = "medium"
        
    # D√©termination de la cat√©gorie
    if "billing" in text or "payment" in text:
        category = "billing"
    elif "bug" in text or "error" in text or "not working" in text:
        category = "technical"
    else:
        category = "general"
        
    return category, priority


# Endpoint pour cr√©er un ticket (utilis√© par l'utilisateur)
# Dans main.py

@app.post("/api/tickets", status_code=201)
def create_ticket(ticket_data: TicketCreate, current_user: User = Depends(get_current_user), db: Session = Depends(get_db)):
    print("--- 1. D√©but de la cr√©ation du ticket ---")
    _, priority = analyze_ticket_content(ticket_data.subject, ticket_data.message)
    
    new_ticket = Ticket(
        user_id=current_user.id,
        subject=ticket_data.subject,
        category=ticket_data.category, # On utilise la cat√©gorie fournie
        priority=priority
    )
    db.add(new_ticket)
    print("--- 2. Ticket ajout√© √† la session SQLAlchemy ---")
    
    try:
        db.commit()
        print("--- 3. COMMIT R√âUSSI pour le ticket ---")
        db.refresh(new_ticket)
        
        initial_message = TicketMessage(
            ticket_id=new_ticket.id,
            author_id=current_user.id,
            message=ticket_data.message
        )
        db.add(initial_message)
        print("--- 4. Message ajout√© √† la session ---")
        
        db.commit()
        print("--- 5. COMMIT R√âUSSI pour le message ---")
        
        return new_ticket
        
    except Exception as e:
        print(f"‚ùå ERREUR PENDANT LE COMMIT : {e}")
        db.rollback() # Annuler la transaction en cas d'erreur
        raise HTTPException(status_code=500, detail="Database error occurred.")

