    # create default admin if none
    with SessionLocal() as db:
        # On v√©rifie si un utilisateur avec cet email ou ce username existe d√©j√†
        user_exists = db.query(User).filter(
            (User.email == "yasmine@fedids.io") | (User.username == "yasmine")
        ).first()
        
        # On ne cr√©e l'admin que s'il n'existe PAS
        if not user_exists:
            print("Admin user 'yasmine' not found, creating new one...")
            admin = User(
                email="yasmine@fedids.io",
                username="yasmine",
                hashed_password=get_password_hash("yasmine"),
                role="admin",
                is_active=True,
            )
            db.add(admin)
            db.commit()
