async def stripe_webhook(request: Request, db: Session = Depends(get_db)):
    payload = await request.body()
    sig_header = request.headers.get('stripe-signature')
    endpoint_secret = os.getenv("STRIPE_WEBHOOK_SECRET")
    try:
        event = stripe.Webhook.construct_event(payload, sig_header, endpoint_secret)
    except Exception as e:
        raise HTTPException(status_code=400, detail=f"Webhook error: {e}")

    if event['type'] == 'payment_intent.succeeded':
        intent = event['data']['object']
        user_id = intent['metadata'].get('user_id')
        if user_id:
            user = db.query(User).filter(User.id == int(user_id)).first()
            if user and user.role != 'premium':
                user.role = 'premium'
                user.subscription_valid_until = datetime.utcnow() + timedelta(days=30)
                db.commit()
    elif event['type'] == 'payment_intent.payment_failed':
        intent = event['data']['object']
        user_id = intent['metadata'].get('user_id')
        print(f"‚ùå Webhook FAILED: Payment failed for user ID {user_id}.")

    return {"status": "success"}



# --- TICKETS (ADMIN) ---

def analyze_ticket_content(subject: str, message: str) -> Tuple[str, str]:
    """
    Analyse le contenu d'un ticket pour d√©terminer sa priorit√©.
    """
    text = (subject + " " + message).lower()
    
    # === LA LOGIQUE DE PRIORIT√â AM√âLIOR√âE EST ICI ===

    # 1. On cherche d'abord les mots-cl√©s les plus critiques
    critical_keywords = ["urgent", "critical", "down", "outage", "cannot work", "production stop"]
    if any(keyword in text for keyword in critical_keywords):
        priority = "critical"
        return priority # On s'arr√™te ici si c'est critique

    # 2. Ensuite, les mots-cl√©s de haute priorit√©
    high_keywords = ["billing", "payment", "invoice", "security issue", "vulnerability"]
    if any(keyword in text for keyword in high_keywords):
        priority = "high"
        return priority

    # 3. Ensuite, les mots-cl√©s de priorit√© moyenne
    medium_keywords = ["bug", "error", "not working", "problem", "issue"]
    if any(keyword in text for keyword in medium_keywords):
        priority = "medium"
        return priority
        
    # 4. Si aucun mot-cl√© correspondant n'est trouv√©, la priorit√© est basse
    # C'est le cas pour les questions g√©n√©rales, les demandes de fonctionnalit√©s, etc.
    priority = "low"
        
    return priority


